        "ssh-keys": `ubuntu:${fs.readFileSync("~/.ssh/id_rsa.pub")}`,
    },
    networkInterfaces: [{
        subnetId: fooVpcSubnet.id,
    }],
    platformId: "standard-v1",
    resources: {
        cores: 2,
        memory: 4,
    },
    zone: "ru-central1-a",
});
