```typescript
import * as pulumi from "@pulumi/pulumi";
import * as yandex from "@pulumiverse/yandex";

const foo = new yandex.MdbKafkaCluster("foo", {
    networkId: "c64vs98keiqc7f24pvkd",
    config: {
        version: "2.8",
        zones: ["ru-central1-a"],
        kafka: {
            resources: {
                resourcePresetId: "s2.micro",
                diskTypeId: "network-hdd",
                diskSize: 16,
            },
        },
    },
});
const events = new yandex.MdbKafkaTopic("events", {
    clusterId: foo.id,
    partitions: 4,
    replicationFactor: 1,
});
const userEvents = new yandex.MdbKafkaUser("userEvents", {
    clusterId: foo.id,
    password: "pass1231232332",
    permissions: [
        {
            topicName: "events",
            role: "ACCESS_ROLE_CONSUMER",
        },
        {
            topicName: "events",
            role: "ACCESS_ROLE_PRODUCER",
        },
    ],
});
