const fooVpcNetwork = new yandex.VpcNetwork("fooVpcNetwork", {});
const fooVpcSubnet = new yandex.VpcSubnet("fooVpcSubnet", {
    networkId: fooVpcNetwork.id,
    v4CidrBlocks: ["10.1.0.0/24"],
    zone: "ru-central1-a",
});
const fooMdbMongodbCluster = new yandex.MdbMongodbCluster("fooMdbMongodbCluster", {
    clusterConfig: {
        version: "4.2",
    },
    databases: [{
        name: "testdb",
    }],
    environment: "PRESTABLE",
    hosts: [{
        subnetId: fooVpcSubnet.id,
        zoneId: "ru-central1-a",
    }],
    labels: {
        test_key: "test_value",
    },
    maintenanceWindow: {
        type: "ANYTIME",
    },
    networkId: fooVpcNetwork.id,
    resourcesMongocfg: {
        diskSize: 14,
        diskTypeId: "network-hdd",
        resourcePresetId: "s2.small",
    },
    resourcesMongod: {
        diskSize: 16,
        diskTypeId: "network-hdd",
        resourcePresetId: "s2.small",
    },
    resourcesMongos: {
        diskSize: 14,
        diskTypeId: "network-hdd",
        resourcePresetId: "s2.small",
    },
    users: [{
        name: "john",
        password: "password",
        permissions: [{
            databaseName: "testdb",
        }],
    }],
});
