```typescript
import * as pulumi from "@pulumi/pulumi";
import * as yandex from "@pulumiverse/yandex";

const bucket = new yandex.StorageBucket("bucket", {
    acl: "private",
    bucket: "my-bucket",
    lifecycleRules: [
        {
            enabled: true,
            expiration: {
                days: 90,
            },
            id: "log",
            prefix: "log/",
            transitions: [{
                days: 30,
                storageClass: "COLD",
            }],
        },
        {
            enabled: true,
            expiration: {
                date: "2020-12-21",
            },
            id: "tmp",
            prefix: "tmp/",
        },
    ],
});
const versioningBucket = new yandex.StorageBucket("versioningBucket", {
    acl: "private",
    bucket: "my-versioning-bucket",
    lifecycleRules: [{
        enabled: true,
        noncurrentVersionExpiration: {
            days: 90,
        },
        noncurrentVersionTransitions: [{
            days: 30,
            storageClass: "COLD",
        }],
        prefix: "config/",
    }],
    versioning: {
        enabled: true,
    },
});
