    bucket: "example-tf-settings-bucket",
    website: {
        indexDocument: "index.html",
        errorDocument: "error.html",
    },
    lifecycleRules: [
        {
            id: "test",
            enabled: true,
            prefix: "prefix/",
            expiration: {
                days: 30,
            },
        },
        {
            id: "log",
            enabled: true,
            prefix: "log/",
            transitions: [{
                days: 30,
                storageClass: "COLD",
            }],
            expiration: {
                days: 90,
            },
        },
        {
            id: "everything180",
            prefix: "",
            enabled: true,
            expiration: {
                days: 180,
            },
        },
        {
            id: "cleanupoldversions",
            prefix: "config/",
            enabled: true,
            noncurrentVersionTransitions: [{
                days: 30,
                storageClass: "COLD",
